    private static boolean firstTime = false;
    /**
     * @param args the command line arguments
     */
    public static void main(String[] args) {
        char [] characters = parentheses.toCharArray();
        int sum = 0;
        int countChars = 0;
        for (char c : characters) {
            countChars++;
            switch (c) {
                case '(' : { sum++; break; }
                case ')' : { sum--; break; }
                default : //no-op
            }
            if (sum < 0 && firstTime == false) {
                firstTime = true;
                System.out.println("Index of first negative: " + countChars);
            }
        }
        System.out.println("Sum: " + sum);
    }
    
